sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {
	//Set vendor based on description
	// Add your code here
	var g = new GlideRecord("x_435483_taxprepar_vendor");
    g.orderBy('priority');
    g.query();
    while (g.next()) {
		//gs.info("debugTax: OUTSIDE the IF statement");
		//gs.info("debugTax: This is the Vendor Table Item: " + g.match);
		//gs.info("debugTax: This is the source data csv Item:" + source.u_description);
        //If there is a match
        if (source.u_description.toUpperCase().indexOf(g.match.toUpperCase()) !== -1) {
			//gs.info("debugTax: INSIDE the if statement! This means THERE WAS A MATCH!---------------------->>>>>> Source: " + source.u_description + " match: " + g.match);
			//gs.info("This is the source data from the csv:" + source.u_description);
			//gs.info("debugTax: This is what is being Labled as the VENDOR: " + g.u_name);
            target.u_vendor_reference.setDisplayValue(g.u_name);
            target.u_category_reference.setDisplayValue(g.category);
            target.u_type.setDisplayValue(g.typetype);
            target.sub_type.setDisplayValue(g.sub_type);
            target.u_country.setDisplayValue(g.country);
			break;
        }

    }
})(source, map, log, target);
